import React, { useState } from 'react';
import {
  SafeAreaView,
  View,
  Text,
  TextInput,
  TouchableOpacity,
} from 'react-native';

import { StyleSheet } from 'react-native';

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  return (
    <SafeAreaView style={{flex: 1, justifyContent: 'center'}}>
      <View style = {styles.container}>

        <View style={styles.input_container}>
          <Text style={styles.input_type}>USERNAME</Text>
          <TextInput
            style = {styles.input}
            value={email} 
            onChangeText={setEmail} 
            placeholder = "Username..."
            autoCapitalize='none'
            autoCorrect = 'off'
            maxLength = {40}
          />
        </View>

        <View style={styles.input_container}>
          <Text style={styles.input_type}>EMAIL         </Text>
          <TextInput
            style = {styles.input}
            value={email} 
            onChangeText={setEmail} 
            placeholder = "Username..."
            autoCapitalize='none'
            autoCorrect = 'off'
            maxLength = {40}
          />
        </View>

        <View style={styles.input_container}>
          <Text style={styles.input_type}>PASSWORD</Text>
          <TextInput 
            style = {styles.input}
            value={password} 
            onChangeText={setPassword} 
            secureTextEntry
            placeholder = "Password..."
            autoCapitalize='none'
            maxLength = {40}
          />
        </View>

        <TouchableOpacity>
          <Text>ARROW SIGN HERE</Text>
        </TouchableOpacity>
      </View>
    </SafeAreaView>
  );
}

export default Login;

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#FDFDFD',
    alignItems: 'center',
    justifyContent: 'center',
  },

  input_container: {
    borderBottomWidth: 1,
    marginBottom: 12,
    flexDirection: 'row'
  },

  input_type: {
    height: 20,
    top: 20,
    marginRight: 30
  },

  input: {
    height: 20,
    width: 200,
    top:8,
    margin: 12
  }
});
